import React from 'react';
import avatar from '../images/avatar.jpg';
// import Card from "./Card.js"
// import Api from "../utils/Api"

function Main({onEditProfile, onAddPlace, onEditAvatar}) {
    const [userName, setUserName] = React.useState("")
    const [userDescription, setUserDescription] = React.useState("")
    const [userAvatar, setUserAvatar] = React.useState("")

    const [cards, setCards] = React.useState("")

    React.useEffect(() => {
        api.getUserInfo()
        .then((res) => {
            setUserName(res.name);
            setUserDescription(res.about);
            setUserAvatar(res.avatar);
        })
        .catch((err) => {console.log(err)});
    }, []);

    React.useEffect(() => {
        api.getInitialCards()
        .then((res) => {
            setCards(res);
        })
        .catch((err) => {console.log(err)});
    }, []);

  return (
    <main className="content">
        <section class="profile">
            <button class="profile__avatar-button" type="button">
                <img class="profile__avatar" src={avatar} alt="Аватар"/>
            </button>
            <div class="profile__info">
                <div class="profile__container">
                    <h1 class="profile__name">Жак-Ив Кусто</h1>
                    <button class="profile__edit-button" type="button" aria-label="Добавить"></button>
                </div>
                <p class="profile__about">Исследователь океана</p>
            </div>
            <button class="profile__add-button" type="button" aria-label="Добавить"></button>
        </section>
        <div class="cards">
        </div>
    </main>
  );
}

export default Main;

