import React from 'react';

function PopupWithForm({ name, title, isOpen, onClose, children, buttonText, onSubmit }) {
  return (
    <>
    <div className={`popup popup_type_${name} ${isOpen ? "popup_opened" : ""} `}>
      <div className="popup__container">
        <button className={`popup__close popup__close_type_${name}`} type="button" onClick={onClose} aria-label="Закрыть"></button>
        <h2 className="popup__title">{title}</h2>
        <form 
          className="popup__form" 
          name={`form-${name}`} 
          noValidate
          onSubmit={onSubmit}
        >
          {children}
          <button className={`popup__save popup__save_type_${name}`} type="submit" aria-label="Сохранить">
            {buttonText}
          </button>
        </form>
      </div>
    </div>
    </>
  );
}
export default PopupWithForm;
