import React, { useEffect, useState } from 'react';
import Card from "./Card"
import Api from "../utils/Api"
import App from './App';


function Main({ onEditProfile, onAddPlace, onEditAvatar, onCardClick }) {

    const [userName, setUserName] = useState("")
    const [userDescription, setUserDescription] = useState("")
    const [userAvatar, setUserAvatar] = useState("")
    const [cards, setCards] = useState([]);

    useEffect(() => {
        Api
          .getUserInfo()
          .then((profileUserInfo) => {
            setUserName(profileUserInfo.name);
            setUserDescription(profileUserInfo.about);
            setUserAvatar(profileUserInfo.avatar);
          })
          .catch((error) => console.log(error));
        Api  
          .getCards()
          .then((cards) => {
            setCards(cards);
          })
          .catch((error) => console.log(error));
    }, []);

  return (
    <main className="content">
        <section class="profile">
            <button 
              class="profile__avatar-button" 
              type="button"
              onClick={onEditAvatar}
            >
                <img class="profile__avatar" src={avatar} alt="Аватар"/>
            </button>
            <div class="profile__info">
                <div class="profile__container">
                    <h1 class="profile__name">Жак-Ив Кусто</h1>
                    <button class="profile__edit-button" type="button" aria-label="Добавить"></button>
                </div>
                <p class="profile__about">Исследователь океана</p>
            </div>
            <button class="profile__add-button" type="button" aria-label="Добавить"></button>
        </section>
        <div class="cards">
        </div>
    </main>
  );
}

export default Main;

