import React from 'react';
import Header from './Header';
import Main from './Main';
import PopupWithForm from './PopupWithForm';
import ImagePopup from './ImagePopup';
import Footer from './Footer';
import api from '../utils/Api'

function App() {
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false)
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState("")
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = React.useState(false)

  const [cards, setCards] = React.useState([]);

  const [selectedCard, setSelectedCard] = useState("");


  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true)
  }

  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true)
  }

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true)
  }
  function closeAllPopups() {
    setIsEditProfilePopupOpen(false);
    setIsAddPlacePopupOpen(false);
    setIsEditAvatarPopupOpen(false);
    setSelectedCard(null);
  }
  function handleCardClick(card) {
    setSelectedCard(card);
  }

  /* Получение информации о пользователе и исходных карточек при открытии страницы */
  React.useEffect(() => {
    api.getCards()
      .then((res) => {
        setCards(res);
      })
      .catch(console.error);
  }, []);

  React.useEffect(() => {
    Api.getCards()
    .then((res) => {
        setCards(res);
    })
    .catch((err) => {console.log(err)});
}, []);


  return (
    <div className="root">
       <div className="page">
         <Header/>
         <Main>
           onEditProfile = {handleEditProfileClick}
           onAddPlace = {handleAddPlaceClick}
           onEditAvatar = {handleEditAvatarClick}
           onCardClick={handleCardClick}
           cards={cards}
         </Main>
         <Footer/>
         <PopupWithForm>
           name="edit"
           buttonText="Сохранить"
           title="Редактировать профиль"
           isOpen={isEditProfilePopupOpen}
           onClose={closeAllPopups}
           <input 
            className="popup__edit-form popup__edit-form_input_name" 
            id="name-input" 
            name="name" 
            type="text" 
            placeholders="Имя" 
            value 
            minlength="2" 
            maxlength="40" 
            required
           />
           <span className="popup__span popup__span_error_visible name-input-error"></span>
           <input 
            className="popup__edit-form popup__edit-form_input_about " 
            id="about-input" 
            name="about" 
            type="text" 
            placeholders="О себе" 
            value 
            minlength="2" 
            maxlength="200" 
            required
           />
           <span className="popup__span popup__span_error_visible about-input-error"></span>
         </PopupWithForm>
         <ImagePopup>
           name="image"
           buttonText="Сохранить"
           isOpen={isAddPlacePopupOpen}
           onClose={closeAllPopups}
         </ImagePopup>
       </div>
    </div>
  );
}
export default App;


